import {useState, useEffect} from 'react'
import { Navigate } from 'react-router-dom'
function Account () {
    const [logStatus, setLogStatus] = useState(false)
    console.log('initial status check', logStatus)

    useEffect(() => {
        async function checkLogin() {
          await fetch('/check')
            .then( r => {
              if (r.ok){
                setisLoggedIn(true);
              }
              else {
                setisLoggedIn(false);
              }
            })
        }
        checkLogin();
      }, []);

    let logoutHandler = () => {
        fetch('/logout', {method: 'DELETE'})
            .then(r => {})
            .then(() => {
                console.log('logout handler called')
                // setLogStatus(false)
                setLog(false)
            })
    }

    let loggedinDisp = () => {
        return (
            <>
                Logged in!
                <br></br>
                <button onClick={() => {logoutHandler()}}>Log Out</button>
            </>
        )
    }

    let loggedoutDisp = () => {
        return (
            <div>
                <br></br>
                How did you get in here?
                <br></br>
                <br></br>
                401: Not Authorized!
                <Navigate replace to='/login' />
            </div>
        )
    }

    return (
        <div>
            {
                console.log('logStatus at return...', logStatus)
            }
            Account
            <br></br>
            {
                logStatus == true? loggedinDisp() : loggedoutDisp()
            }
        </div>
    )

}

export default Account
