import {useContext, useState} from 'react'
import { Navigate } from 'react-router-dom'
import { UserContext } from '../context/user'
function Account () {
    const {user, setUser} = useContext(UserContext)
    const [showEdit, setShowEdit] = useState(false)

    if (user !== null) {
        document.title=`${user.name}'s Account`
    }


    let logoutHandler = () => {
        fetch('/logout', {method: 'DELETE'})
            .then(r => {
                if (r.ok) {
                    setUser(null)
                }
            })
        console.log('logout')
    }

    let handleEdit

    let loggedinDisp = () => {
        return (
            <>
                Logged in!
                <br></br>
                <button onClick={() => {logoutHandler()}}>Log Out</button>
                <div className='acountInfo'>
                    <h2 hidden={showEdit}>Name: {user.name}</h2>
                    <h2 hidden={showEdit}>Username: {user.username}</h2>
                    <h2 hidden={showEdit}>Email: {user.email}</h2>
                    <h2 hidden={showEdit}>Account Type: {user.account_type == 0? 'User' : 'Seller'}</h2>
                    <h2 hidden={showEdit}>Age: {user.age}</h2>
                    <h2 hidden={showEdit}>Change Password</h2>
                    <button onClick={() => setShowEdit(!showEdit)}>Edit</button>
                    <form>
                    <h2 hidden={!showEdit}>Name: {user.name}</h2>
                    <h2 hidden={!showEdit}>Username: {user.username}</h2>
                    <h2 hidden={!showEdit}>Email: {user.email}</h2>
                    <h2 hidden={!showEdit}>Account Type: {user.account_type == 0? 'User' : 'Seller'}</h2>
                    <h2 hidden={!showEdit}>Age: {user.age}</h2>
                    <h2 hidden={!showEdit}>Change Password</h2>
                    <button onClick={() => setShowEdit(!showEdit)}>Submit</button>
                    </form>
                </div>
            </>
        )
    }

    let loggedoutDisp = () => {
        return (
            <div>
                <br></br>
                How did you get in here?
                <br></br>
                <br></br>
                401: Not Authorized!
                <Navigate replace to='/login' />
            </div>
        )
    }

    let dispChange = user? loggedinDisp() : loggedoutDisp()

    return (
        <div>
            Account
            <br></br>
            {
                dispChange
            }
        </div>
    )

}

export default Account
