import {useState, useEffect} from 'react'
import { Navigate } from 'react-router-dom'
function Account ({logStatus}) {
    console.log(logStatus)

    let logoutHandler = () => {
        fetch('/logout', {
            method: 'DELETE'
        })
        .then(r => r.json())
        .then(r => {logoutHandler(false)})
    }

    let loggedinDisp = () => {
        return (
            <>
                Logged in!
                <br></br>
                <button onClick={() => {logoutHandler()}}>Log Out</button>
            </>
        )
    }

    let loggedoutDisp = () => {
        return (
            <div>
                <br></br>
                How did you get in here?
                <br></br>
                <br></br>
                401: Not Authorized!
                <Navigate replace to='/login' />
            </div>
        )
    }

    return (
        <div>
            Account
            <br></br>
            {logStatus? loggedinDisp() : loggedoutDisp()}
        </div>
    )

}

export default Account
