import { useContext, useState, useEffect} from "react"
import { UserContext } from "../context/user"

function Cart () {
    document.title='Cart'
    const {user, setUser} = useContext(UserContext)
    const [carts, setCarts] = useState([])
    const [currentCart, setCurrentCart] = useState({})
    const [stateItems, setStateItems] = useState([])
    useEffect(() => {
        if (!user) {
            fetch('/check').then(r => {
                if (r.ok) {
                    return r.json();
                }
                else {
                    setUser({
                        'name': 'Guest',
                        'user_carts': [
                            {
                                'name': 'GuestCart',
                                'items': []
                            }
                        ]
                    });
                    console.log('welcome, guest');
                }
            })
            .then(body => {
                setUser(body);
                console.log('checked user', body.name);

            })
        }
        else{
            setCarts(user.user_carts)
            setCurrentCart(carts[0])
            console.log('current cart set', currentCart)
            setStateItems(currentCart.items)
            console.log('stateItems set', stateItems)
        }

    },[user])

    let returnDef = () => {
        return (<>noUser</>)
    }
    if (!user) {
        return returnDef()
    }

    let cartList = user.user_carts.map((cart) => {
        return (<option value={cart.id-1}>{cart.name}</option>)
    })

    let renderCart = stateItems.map((item) => {
        return (<p>{item.title}</p>)
    })


    let handleDropChange = (e) => {
        console.log(e.target.value)
        setCurrentCart(carts[e.target.value])
        setStateItems(currentCart.items)
    }

    return (
        <div className="CartPage" >
            <div className="dropDown">
                <select onChange={handleDropChange} defaultValue={0}>
                    {cartList}
                </select>
            </div>
            <div className='details'>
                {renderCart}
            </div>
            <div>

            </div>
        </div>
    )
}

export default Cart
