import {useState, useEffect} from 'react'
import { Navigate } from 'react-router-dom'
async function Account () {
    const [isLoggedIn, setisLoggedIn] = useState(false)

    let logoutHandler = () => {
        fetch('/logout', {
            method: 'DELETE'
        })
    }

    let loggedinDisp = () => {
        return (
            <>
                Logged in!
                <br></br>
                <button onClick={logoutHandler}>Log Out</button>
            </>
        )
    }

    let loggedoutDisp = () => {
        return (
            <div>
                <br></br>
                How did you get in here?
                <br></br>
                <br></br>
                401: Not Authorized!
                <Navigate replace to='/login' />
            </div>
        )
    }
        fetch('/check')
        .then(r =>{
            if (r.ok) {
                if (isLoggedIn == true){
                    return r.json()
                }
                else{
                    setisLoggedIn(true)
                    console.log(r.ok)
                }
            }
            else {
                if (isLoggedIn == false)
                {
                    return r.json()
                }
                setisLoggedIn(false)
            }
            return r.json()
        })
        .then(resp => {
            console.log(resp)

        })

    return (
        <div>
            Account
            <br></br>
            {isLoggedIn? loggedinDisp() : loggedoutDisp()}
        </div>
    )

}

export default Account
