import {useState, useEffect} from 'react'
import { Navigate } from 'react-router-dom'
async function Account () {
    const [isLoggedIn, setisLoggedIn] = useState(false)

    let logoutHandler = () => {
        fetch('/logout', {
            method: 'DELETE'
        })
    }

    let loggedinDisp = () => {
        return (
            <>
                Logged in!
            </>
        )
    }

    let loggedoutDisp = () => {
        return (
            <div>
                <br></br>
                How did you get in here?
                <br></br>
                <br></br>
                401: Not Authorized!
                <Navigate replace to='/login' />
            </div>
        )
    }

    useEffect( () => {
        fetch('/check')
        .then(r =>{
            if (r.ok) {
                setisLoggedIn(true)
                console.log(r.ok)
            }
            else {
                setisLoggedIn(false)
            }
            return r.json()
        })
        .then(resp => {
            console.log(resp)

        })
    }, [isLoggedIn])


    return (
        <div>
            Account
            <br></br>
            {isLoggedIn? loggedinDisp() : loggedoutDisp()}
            <br></br>
            <button onClick={logoutHandler}>Log Out</button>
        </div>
    )

}

export default Account
