import {useContext, useState} from 'react'
import { Navigate } from 'react-router-dom'
import { UserContext } from '../context/user'
function Account () {
    const {user, setUser} = useContext(UserContext)
    const [showEdit, setShowEdit] = useState(false)

    if (user !== null) {
        document.title=`${user.name}'s Account`
    }


    let logoutHandler = () => {
        fetch('/logout', {method: 'DELETE'})
            .then(r => {
                if (r.ok) {
                    setUser(null)
                }
            })
        console.log('logout')
    }

    let handleEdit = (e) => {   
        e.preventDefault()
        console.log((e.target[0].value))
        for (let i = 0; i < 6; i++) {
            if (e.target[i].value == null) {
                let temp = (e.target[i].name).toLowerCase()
                e.target[i].value = user.temp
            }

        }
        // fetch(`/user/${user.id}`, {
        //     method: 'PATCH',
        //     headers: {
        //         'Content-Type': 'application/json'
        //     },
        //     body: JSON.stringify({
        //         '':''
        //     })
        // })
        setShowEdit(!showEdit)
    }

    let loggedinDisp = () => {
        return (
            <>
                Logged in!
                <br></br>
                <button onClick={() => {logoutHandler()}}>Log Out</button>
                <div className='acountInfo'>
                    <h2 hidden={showEdit}>Name: {user.name}</h2>
                    <h2 hidden={showEdit}>Username: {user.username}</h2>
                    <h2 hidden={showEdit}>Email: {user.email}</h2>
                    <h2 hidden={showEdit}>Account Type: {user.account_type == 0? 'User' : 'Seller'}</h2>
                    <h2 hidden={showEdit}>Age: {user.age}</h2>
                    <button hidden={showEdit} onClick={() => setShowEdit(!showEdit)}>Edit</button>
                    <form onSubmit={handleEdit}>
                    <h2 hidden={!showEdit}>Name: <input name='name' placeholder={user.name}></input></h2>
                    <h2 hidden={!showEdit}>Username: <input name='name' placeholder={user.username}></input></h2>
                    <h2 hidden={!showEdit}>Email: <input name='name' placeholder={user.email}></input></h2>
                    <h2 hidden={!showEdit}>Age: <input name='name' placeholder={user.age}></input></h2>
                    <h2 hidden={!showEdit}>Change Password: <input name='name'></input></h2>
                    <button hidden={!showEdit}>Submit</button>
                    </form>
                </div>
            </>
        )
    }

    let loggedoutDisp = () => {
        return (
            <div>
                <br></br>
                How did you get in here?
                <br></br>
                <br></br>
                401: Not Authorized!
                <Navigate replace to='/login' />
            </div>
        )
    }

    let dispChange = user? loggedinDisp() : loggedoutDisp()

    return (
        <div>
            Account
            <br></br>
            {
                dispChange
            }
        </div>
    )

}

export default Account
