//Mark Coats Capstone Project 03/25/2023
import {React, useState, useRef, useEffect, createContext} from 'react';
import {Routes, Route} from 'react-router-dom';
import Cart from './Cart';
import Homepage from './Homepage';
import Navbar from './Navbar';
import Products from './Products';
import Account from './Account'
import Login from './Login'
import '../App.css';

function App() {
  const [isLoggedIn, setisLoggedIn] = useState(false);
  const [isWaiting, setisWaiting] = useState(true);
  const [user, setUser] = useState({});
  console.log('isWaiting', isWaiting);
  const logRef = useRef({});
  logRef.current=isLoggedIn;
  document.title = "Mark's Site";

  useEffect(() => {
    async function checkLogin() {
      await fetch('/check')
        .then( r => {
          if (r.ok){
            setisLoggedIn(true);
          }
          else {
            setisLoggedIn(false);
          }
        })
        .then(() => {
          setisWaiting(false)
        })
    }
    checkLogin();
  }, [isLoggedIn]);

  let handleSetUser = (userIn) => {
    setUser(userIn)
  }



  let loginHandler = (boo) => {
    setisLoggedIn(boo);
  }

  let appHandler = () => {
    return (
      <UserContext.Provider value={user} >
        <div className="App">
          {console.log('app login status', isLoggedIn)}
          <Navbar />
          <Routes>
            <Route 
              element={< Homepage />}
              path='/'
              />
            <Route 
              element={< Products />}
              path='/products'
              />
            <Route 
              element={< Cart />}
              path='/cart'
              />
            <Route 
              element={< Account setLog={loginHandler} logStatus={isLoggedIn} wait={isWaiting}/>}
              path='/account'
              />
            <Route 
              element={< Login setLog={loginHandler} logStatus={isLoggedIn} wait={isWaiting} setUser={setUser}/>}
              path='/login'
              />
          </Routes>

        </div>
      </UserContext.Provider>
    );
  }
  return (
    <div>
      {
        isWaiting? <></> : appHandler()
      }
    </div>
  )

}

export default App;
