import { useContext } from "react"
import { UserContext } from "../context/user"
import { useState, useEffect } from "react"

function Cart () {
    document.title='Cart'
    const {user} = useContext(UserContext)
    const [userData, setUserData] = useState(null)
    const [cartDetails, setCartDetails] = useState(null)
    console.log(user)
    useEffect(() => {
        fetch('/check')
            .then(r=>r.json())
            .then(body => {
                setUserData(body)
            })
    }, [])
    // console.log(userData)
    let defScreen = () => {
        return (<></>)
    }
    getCartDetails(1)
    if (userData == null || cartDetails == null) {
        return defScreen()
    }
    let renderCartList = userData.user_carts.map(
        (cart) => {
            return (<option key={`cart${cart.id}`} value={cart.id}>{cart.name}</option>)
        }
    )

    let handleSelectChange = (e) => {
        console.log(e.target.value)
        getCartDetails(e.target.value)
    }

    let renderCartItems = cartDetails.items.map((item) => {
            <div key={`item${item.id}`}className="itemCard">
                <h4>{item.title}</h4>
            </div>
        })

    let cardRenderWait = () => {
        return (
            <div className="CartDetails">
                <h2>{cartDetails.name}</h2>
                <h2>{cartDetails.paid}</h2>
                {console.log((cartDetails.items[0]))}
            </div>
        )
    }

    return (
        <>
            <div className="CartListContainer">
                <select onChange={handleSelectChange} defaultValue={'1'}>
                    {renderCartList}
                </select>
            </div>
            {cartDetails? cardRenderWait() : defScreen()}
            <div className="Options">

            </div>
        </>
    )
}

export default Cart
