import ProductCard from "./ProductCard"
import { useContext, useState, useEffect } from "react"
import { UserContext } from "../context/user"

function Products () {
    const {user, setUser} = useContext(UserContext)
    const [carts, setCarts] = useState([{'name': 'default'}])
    const [currentCart, setCurrentCart] = useState(carts[0])
    const [searchState, setSearchState] = useState('')
    const [productList, setProductList] = useState([])
    useEffect(() => {
        fetch('/items').then(r => r.json()).then(body => {setProductList(body)})
        if (!user) {
            fetch('/check').then(r => {
                if (r.ok) {
                    return r.json();
                }
                else {
                    setUser({
                        'name': 'Guest',
                        'user_carts': [
                            {
                                'name': 'GuestCart',
                                'items': []
                            }
                        ]
                    });
                    console.log('welcome, guest');
                }
            })
            .then(body => {
                setUser(body);

            })
        }
        else{
            setCarts(user.user_carts)
            setCurrentCart(carts[0])
        }

    },[user])

    let getCIID = (cart, item) => {

    }

    let handleAddClick = (e) => {
        console.log(currentCart.id)
        let id = e.target.id
        let itemincart = currentCart.items.filter(item => {
            return (item.id == id)
        })
        console.log(itemincart)
        if (itemincart.length > 0) {
            console.log('item in cart')
            let ciid = currentCart.cart_items.filter(ci => {
                return (ci.item_id == id && ci.cart.id == currentCart.id)
            })
            console.log(ciid, 'ciid')
            fetch(`/cart_items/${id}`, {
                method: 'PATCH',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({'quantity': ciid.quantity + 1})
            })
        }
        else {
            console.log('item not in cart')
        }
    }
    let renderProducts = productList.map((product) => {
        return (
            <div className="productCard" key={`productCard-${product.key}`}>
                <img src={product.img} alt={product.img} />
                <h2>{product.title}</h2>
                <p>{product.price}</p>
                <button id={product.id} onClick={handleAddClick}>Add to Cart</button>
            </div>
        )
    })


    return (
        <div className="productPage">
            <div className="search">
                <form id='searchform'>
                    <input name='searchfield' placeholder="Search..."></input>
                    <button type='submit'>üîç</button>
                </form>
            </div>
            <div className="options">

            </div>
            <div className="productContainer">
                {renderProducts}
            </div>
        </div>
    )

}

export default Products
